	--VR>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Tesouraria <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--

	/*
	Objetivo: Retornar o valor realizado da transacao da tesouraria  passada por parametro no projeto recurso passado por parametro.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nr_transacao_p = Numero de sequencia da transacao.
	cd_estabelecimento_p = Codigo do estabelecimento.
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_realizado_te ( nr_seq_proj_rec_p bigint, nr_transacao_p bigint default 0, cd_estabelecimento_p bigint DEFAULT NULL) RETURNS bigint AS $body$
DECLARE

	
	vl_total_te_w			movto_trans_financ.vl_transacao%type;
	ie_tesouraria_proj_rec_w	parametro_compras.ie_tesouraria_proj_rec%type;
	
	
BEGIN

	vl_total_te_w := 0;
	
	select 	coalesce(max(ie_tesouraria_proj_rec),'N')
	into STRICT	ie_tesouraria_proj_rec_w
	from   	parametro_compras
	where  	cd_estabelecimento = cd_estabelecimento_p;
	
	
	if (ie_tesouraria_proj_rec_w = 'S') then
	
		select  coalesce(sum(vl_transacao),0)
		into STRICT	vl_total_te_w
		from 	movto_trans_financ
		where 	nr_seq_proj_rec = nr_seq_proj_rec_p
		and (nr_transacao_p = 0 or nr_sequencia = nr_transacao_p)
		and 	coalesce(nr_seq_titulo_pagar::text, '') = ''
		and 	coalesce(nr_seq_nota_fiscal::text, '') = ''
		and 	coalesce(nr_seq_titulo_receber::text, '') = ''
		and 	(nr_seq_caixa IS NOT NULL AND nr_seq_caixa::text <> '');
		
		
	end if;
	
	return	vl_total_te_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
