	--VR>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Titulo a Pagar <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--

	/*
	Objetivo: Retornar o valor realizado no projeto do titulo a pagar passado por parametro.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nr_titulo_p = Numero de sequencia do titulo a pagar
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_realizado_tp ( nr_seq_proj_rec_p bigint, nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE

	
	vl_real_w		titulo_pagar.vl_titulo%type;
	
	
BEGIN
	
	select   coalesce(sum(projeto_recurso_pck.obter_vl_tit_pag(nr_titulo)),0)
	into STRICT 	vl_real_w
	from    titulo_pagar a
	where   a.nr_seq_proj_rec = nr_seq_proj_rec_p
	and     ie_situacao in ('A','L','T','B')-- Considera titulos a pagar com status: Aberto, Liquidado, Transferido e Bloqueado
	and	coalesce(nr_seq_nota_fiscal::text, '') = ''
	and     nr_titulo = nr_titulo_p;
	
	return	vl_real_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
