-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>++++++ VALORES DE SALDO ++++++<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--

	--VS>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total de saldo no projeto recurso <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--

	/*
	Objetivo: Retornar o valor de saldo restante no projeto recurso passado por parametro.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nm_usuario_p = Nome do usuario.
	cd_estabelecimento_p = Codigo do estabelecimento logado pelo usuario.
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_saldo_proj_rec ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

	
	/* Valor de saldo do projeto recurso */
		
	vl_saldo_proj_rec_w		projeto_recurso_saldo.vl_saldo%type;

	/* Variavel para armazenar o valor total do projeto recurso.*/


	vl_projeto_w			projeto_recurso.vl_inicio_projeto%type;		
	
	/* Serve para identificar se o valor comprometido deve ser diminuido do saldo do projeto. */


	ie_considera_vl_compr_saldo_w   parametro_compras.ie_considera_vl_compr_saldo%type;
		
	
BEGIN
	
	vl_saldo_proj_rec_w := 0;
	
	vl_projeto_w := projeto_recurso_pck.obter_vl_proj_rec(nr_seq_proj_rec_p);
	
	select 	coalesce(ie_cons_solic_acum_proj_rec,'N')
	into STRICT	ie_considera_vl_compr_saldo_w
	from   	parametro_compras
	where  	cd_estabelecimento = cd_estabelecimento_p;
	
	if (ie_considera_vl_compr_saldo_w = 'S') then
		vl_saldo_proj_rec_w := vl_projeto_w - (projeto_recurso_pck.obter_vl_realizado(nr_seq_proj_rec_p, nm_usuario_p, cd_estabelecimento_p) + projeto_recurso_pck.obter_vl_comprometido(nr_seq_proj_rec_p, nm_usuario_p, cd_estabelecimento_p));
	else
		vl_saldo_proj_rec_w := vl_projeto_w - projeto_recurso_pck.obter_vl_realizado(nr_seq_proj_rec_p, nm_usuario_p, cd_estabelecimento_p);
	end if;
		
	return	coalesce(vl_saldo_proj_rec_w,0);
	
	END;	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
