SET client_encoding TO 'UTF8';

	
	
	--VT>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total de notas fiscais do projeto recurso <<<<<<<<<<<<<<<<<<<<<<<<<<--	

	/*
	Objetivo: Retornar o valor total das notas fiscais do projeto recurso.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nm_usuario_p = Nome do usuario.
	cd_estabelecimento_p = Codigo do estabelecimento logado pelo usuario.
	*/
	


CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_total_nf_proj_rec ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

	
	/* Variavel para retornar o valor total de notas fiscais do projeto recurso.*/


	vl_total_nf_proj_rec		projeto_recurso_saldo.vl_saldo%type;	
		
	/* Armazena o numero nota fiscal obtida pelo cursor c01 */


	nr_seq_nf_w			nota_fiscal.nr_sequencia%type;
	
	
	/* Notas fiscais*/
			
	c01 CURSOR FOR
		SELECT  a.nr_sequencia
		from    nota_fiscal a,
			operacao_nota b
		where   a.ie_situacao = 1
		and     (a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
		and	a.cd_operacao_nf = b.cd_operacao_nf
		and	b.ie_operacao_fiscal = 'E'
		and     exists (SELECT 	1
				from 	nota_fiscal_item b
				where	a.nr_sequencia = b.nr_sequencia
				and 	b.nr_seq_proj_rec = nr_seq_proj_rec_p);
	
	
	
BEGIN
	
	vl_total_nf_proj_rec := 0;

	open C01;
	loop
	fetch C01 into	
		nr_seq_nf_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		vl_total_nf_proj_rec := vl_total_nf_proj_rec + projeto_recurso_pck.obter_vl_realizado_nf(nr_seq_proj_rec_p, nr_seq_nf_w);
		
		end;
	end loop;
	close C01;
	
	
	return vl_total_nf_proj_rec;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
