SET client_encoding TO 'UTF8';

	
	
	--VT>>>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total de ordens de compra do projeto recurso <<<<<<<<<<<<<<<<<<<<<<<<<<--	

	/*
	Objetivo: Retornar o valor total das ordens de compra do projeto recurso.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nm_usuario_p = Nome do usuario.
	cd_estabelecimento_p = Codigo do estabelecimento logado pelo usuario.
	*/
	


CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_total_oc_proj_rec ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

	
	/* Variavel para retornar o valor total de ordens de compra do projeto recurso.*/


	vl_total_ordem_proj_rec		projeto_recurso_saldo.vl_saldo%type;	
		
	/* Armazena o numero da ordens de compra obtida pelo cursor c01 */


	nr_seq_ordem_w			ordem_compra.nr_ordem_compra%type;
	
	
	/* Ordens de compra */
			
	c01 CURSOR FOR
		SELECT  a.nr_ordem_compra
		from    ordem_compra a
		where   (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and     coalesce(a.nr_seq_motivo_cancel::text, '') = ''
		and     exists (SELECT  1
				from    ordem_compra_item b
				where   a.nr_ordem_compra = b.nr_ordem_compra
				and     b.nr_seq_proj_rec = nr_seq_proj_rec_p);
	
	
	
BEGIN
	
	vl_total_ordem_proj_rec := 0;

	open C01;
	loop
	fetch C01 into	
		nr_seq_ordem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		vl_total_ordem_proj_rec := vl_total_ordem_proj_rec + projeto_recurso_pck.obter_vl_comprometido_ordem(nr_seq_proj_rec_p, nr_seq_ordem_w);
		
		end;
	end loop;
	close C01;
	
	
	return vl_total_ordem_proj_rec;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
