	/*
	Objetivo: Retornar o valor inicial total do projeto recurso (Valor do projeto + valor dos subprojetos + soma de valores transferidos)
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	nm_usuario_p = Nome do usuario.
	cd_estabelecimento_p = Codigo do estabelecimento logado pelo usuario.
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_total_proj_rec ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

	
	/* Variavel para retornar o valor total do projeto recurso.*/


	vl_total_proj_rec		projeto_recurso_saldo.vl_saldo%type;	
		
	
BEGIN
	
	vl_total_proj_rec := projeto_recurso_pck.obter_vls_sub_proj_rec(nr_seq_proj_rec_p) + projeto_recurso_pck.obter_vl_proj_rec(nr_seq_proj_rec_p);

	return coalesce(vl_total_proj_rec,0);
	
	END;

