-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>++++++ VALORES TRANSFERIDOS ++++++<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<-

	--VTR>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Obtem o valor transeferido do projeto (Entrada ou Saida) <<<<<<<<<<<<<<<<<<<<<<<<<<<--	

	/*
	Objetivo: Obter o valor total transferido de entrada ou saida para o projeto passado por parametro.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	ie_entrada_saida_p = Flag para definir se o retorno sera o bvalor transferido de entrada ou saida; E = Entrada S = Saida.
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vl_trans_proj_rec ( nr_seq_proj_rec_p bigint, ie_entrada_saida_p text) RETURNS bigint AS $body$
DECLARE


	vl_transferido_w   proj_recurso_trans_saldo.vl_transferido%type;
						
	
BEGIN
	
	select 	coalesce(sum(vl_transferido),0)
	into STRICT	vl_transferido_w
	from 	proj_recurso_trans_saldo
	where 	nr_seq_proj_rec = nr_seq_proj_rec_p
	and 	ie_entrada_saida = ie_entrada_saida_p;
	
	return vl_transferido_w;
		
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
