-->>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>++++++ VALORES DE SUBPROJETOS ++++++<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--

	--VSP>>>>>>>>>>>>>>>>>>>>>>>>> Obter valor total inicial dos subprojetos vinculados ao projeto recurso <<<<<<<<<<<<<<<<<<<<<<<<<--

	/*
	Objetivo: Retornar valor total inicial dos subprojetos vinculados ao projeto recurso.
	Parametros: 
	nr_seq_proj_rec_p = Numero de sequencia do projeto recurso.
	*/



CREATE OR REPLACE FUNCTION projeto_recurso_pck.obter_vls_sub_proj_rec (nr_seq_proj_rec_p bigint) RETURNS bigint AS $body$
DECLARE

		
	vl_sub_projeto_w	projeto_recurso.vl_inicio_projeto%type;	
		
	
BEGIN
	
		select 	coalesce(sum(projeto_recurso_pck.obter_vl_proj_rec(nr_sequencia)),0)
		into STRICT	vl_sub_projeto_w
		from 	projeto_recurso
		where	nr_seq_superior = nr_seq_proj_rec_p
		and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '');
		
	return vl_sub_projeto_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
