CREATE OR REPLACE PROCEDURE projeto_recurso_pck.set_vl_qt_nota_fiscal ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE
	
	
	/* Variavel para retornar a quantidade total de notas fiscais do projeto recurso.*/


	qt_total_nf_proj_rec		bigint;		
						
	/* Projeto Recurso */
		
	vl_real_proj_rec_w		projeto_recurso_saldo.vl_saldo%type;
	
	/* Nota Fiscal */


	nr_seq_nf_w			nota_fiscal.nr_sequencia%type;
	
	
	c01 CURSOR FOR
		SELECT  a.nr_sequencia
		from    nota_fiscal a,
			operacao_nota b
		where   a.ie_situacao = 1
		and     (a.dt_atualizacao_estoque IS NOT NULL AND a.dt_atualizacao_estoque::text <> '')
		and	a.cd_operacao_nf = b.cd_operacao_nf
		and	b.ie_operacao_fiscal = 'E'
		and     exists (SELECT 	1
				from 	nota_fiscal_item b
				where	a.nr_sequencia = b.nr_sequencia
				and 	b.nr_seq_proj_rec = nr_seq_proj_rec_p);

		
		
	
BEGIN
	
	qt_total_nf_proj_rec := 0;
	vl_real_proj_rec_w := 0;
	
	open C01;
	loop
	fetch C01 into	
		nr_seq_nf_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		qt_total_nf_proj_rec := qt_total_nf_proj_rec + 1;
		vl_real_proj_rec_w := vl_real_proj_rec_w + projeto_recurso_pck.obter_vl_realizado_nf(nr_seq_proj_rec_p,nr_seq_nf_w);
		end;
	end loop;
	close C01;
	
	current_setting('projeto_recurso_pck.nota_fiscal_wr')::nota_fiscal_ty.qt_transacao := qt_total_nf_proj_rec;
	current_setting('projeto_recurso_pck.nota_fiscal_wr')::nota_fiscal_ty.vl_transacao := vl_real_proj_rec_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
