	--SV>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>> Insere valores  variaveis da tesouraria <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<--



CREATE OR REPLACE PROCEDURE projeto_recurso_pck.set_vl_qt_tesouraria ( nr_seq_proj_rec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE
	
	
	/* Variavel para retornar a quantidade total de transacoes da tesouraria no projeto recurso.*/


	qt_total_te_proj_rec		bigint;		
						
	/* Projeto Recurso */
		
	vl_real_proj_rec_w		projeto_recurso_saldo.vl_saldo%type;
	
	
BEGIN
	
	qt_total_te_proj_rec := 0;
	vl_real_proj_rec_w := 0;
	
	
	select  count(1)
	into STRICT	qt_total_te_proj_rec
	from 	movto_trans_financ
	where 	nr_seq_proj_rec = nr_seq_proj_rec_p
	and 	coalesce(nr_seq_titulo_pagar::text, '') = ''
	and 	coalesce(nr_seq_nota_fiscal::text, '') = ''
	and 	coalesce(nr_seq_titulo_receber::text, '') = ''
	and 	(nr_seq_caixa IS NOT NULL AND nr_seq_caixa::text <> '');

	select  coalesce(sum(vl_transacao),0)
	into STRICT	vl_real_proj_rec_w
	from	movto_trans_financ
	where 	nr_seq_proj_rec = nr_seq_proj_rec_p
	and 	coalesce(nr_seq_titulo_pagar::text, '') = ''
	and 	coalesce(nr_seq_nota_fiscal::text, '') = ''
	and 	coalesce(nr_seq_titulo_receber::text, '') = ''
	and 	(nr_seq_caixa IS NOT NULL AND nr_seq_caixa::text <> '');
	
	current_setting('projeto_recurso_pck.tesouraria_wr')::tesouraria_ty.qt_transacao := qt_total_te_proj_rec;
	current_setting('projeto_recurso_pck.tesouraria_wr')::tesouraria_ty.vl_transacao := vl_real_proj_rec_w;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
