CREATE OR REPLACE PROCEDURE ptu_a100_simplificado_pck.atualizar_status_pessoa ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, ie_tipo_segurado_p pls_segurado.ie_tipo_segurado%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

	
	cd_pessoa_fisica_w	pessoa_fisica.cd_pessoa_fisica%type;
	
	
BEGIN
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	pls_segurado
	where	coalesce(ie_tipo_segurado_p, ie_tipo_segurado) in ('I', 'H')
	and	nr_sequencia = nr_seq_segurado_p;
	
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		update	pessoa_fisica
		set	ie_status_usuario_event = 'A',
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	cd_pessoa_fisica = cd_pessoa_fisica_w
		and	coalesce(ie_status_usuario_event, 'X') <> 'A';
	end if;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
