CREATE OR REPLACE PROCEDURE ptu_a100_simplificado_pck.desfazer_lote ( nr_seq_lote_p ptu_intercambio_lote_receb.nr_sequencia%type) AS $body$
DECLARE

	
	dt_confirmacao_w	ptu_intercambio_lote_receb.dt_confirmacao%type;
	
	
BEGIN
	
	select	dt_confirmacao
	into STRICT	dt_confirmacao_w
	from	ptu_intercambio_lote_receb
	where	nr_sequencia = nr_seq_lote_p;
	
	if (dt_confirmacao_w IS NOT NULL AND dt_confirmacao_w::text <> '') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1084429);
	end if;
	
	
	delete	from	ptu_intercambio_benef_simp
	where	nr_seq_lote_receb = nr_seq_lote_p;
	
	delete from 	ptu_intercambio_lote_receb
	where		nr_sequencia	= nr_seq_lote_p;
	
	END;

$body$
