-- Carregar arquivo XML do A560
CREATE OR REPLACE PROCEDURE ptu_a560_imp_pck.carregar_arq_xml_a560 ( ie_tipo_arquivo_p ptu_aviso_arq_xml.ie_tipo_arquivo%type, ds_arquivo_xml_p text, nm_arquivo_p ptu_aviso_arq_xml.nm_arquivo%type, nr_seq_arq_xml_p INOUT ptu_aviso_arq_xml.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
BEGIN

if (coalesce(nr_seq_arq_xml_p::text, '') = '') then
	insert into ptu_aviso_arq_xml( nr_sequencia,				dt_atualizacao,			nm_usuario,
					dt_atualizacao_nrec,			nm_usuario_nrec,		ds_arquivo,
					ie_tipo_arquivo,			nm_arquivo)		
				values (nextval('ptu_aviso_arq_xml_seq'),		clock_timestamp(),			nm_usuario_p,
					clock_timestamp(),				nm_usuario_p,			ds_arquivo_xml_p,
					ie_tipo_arquivo_p,			nm_arquivo_p) 			returning nr_sequencia into nr_seq_arq_xml_p;
					
elsif (nr_seq_arq_xml_p IS NOT NULL AND nr_seq_arq_xml_p::text <> '') then
	-- Preencher arquivo XML
	update	ptu_aviso_arq_xml
	set	ds_arquivo	= ds_arquivo || ds_arquivo_xml_p
	where	nr_sequencia	= nr_seq_arq_xml_p;
end if;	

commit;

END;

$body$
