CREATE OR REPLACE PROCEDURE ptu_a950_pck.carregar_xml ( nr_seq_arq_xml_p INOUT pls_imp_arq_A950.nr_sequencia%type, ds_arquivo_xml_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Importa o XML para o banco
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[ ]  Objetos do dicionario [X] Tasy (Delphi/Java) [ X] Portal [ X]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (nr_seq_arq_xml_p IS NOT NULL AND nr_seq_arq_xml_p::text <> '') then

	update	pls_imp_arq_A950
	set	ds_xml		= ds_xml||ds_arquivo_xml_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_arq_xml_p;
	
	commit;
else
	
	select	nextval('pls_imp_arq_a950_seq')
	into STRICT	nr_seq_arq_xml_p
	;
	
	insert into pls_imp_arq_A950(	nr_sequencia,
					cd_estabelecimento,
					cd_unimed_origem,
					dt_geracao,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_transacao,
					dt_inicio_vigencia,
					ds_xml)
	values (nr_seq_arq_xml_p,
		cd_estabelecimento_p,
		null,
		null,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		ds_arquivo_xml_p);
	commit;

end if;

END;

