SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION ptu_a950_pck.obter_conteudo_tag ( ds_arquivo_xml_p pls_imp_arq_A950.ds_xml%type, ds_tag_p text, nr_tag_p bigint, nr_tag_fim_p bigint, ie_manter_tag_externa_p text) RETURNS text AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Recebe o conteudo a ser analisado, e extrai o valor da tag XML
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[ ]  Objetos do dicionario [X] Tasy (Delphi/Java) [ X] Portal [ X]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w    text;
-- posicao pro substr
nr_substr_ini_w    bigint;
nr_substr_fim_w    bigint;
-- posicao pro instr
nr_instr_ini_w    bigint;
nr_instr_fim_w    bigint;
-- posicao
nr_pos_w    	bigint := 1;
nr_tag_w    	bigint := 1;
nr_tag_fim_w	bigint := coalesce(coalesce(nr_tag_fim_p,nr_tag_p),1);
-- Tag
ds_tag_ini_w    varchar(255) := ptu_a950_pck.montar_tag('I',ds_tag_p);
ds_tag_fim_w    varchar(255) := ptu_a950_pck.montar_tag('F',ds_tag_p);



BEGIN

-- pega o inicio (no XML do sadt, tem tag com aninhamento duplicado, exemplo <sadt>...<sadt></sadt>...</sadt>
while(nr_tag_w <= nr_tag_p) loop

	nr_instr_ini_w	:= instr(ds_arquivo_xml_p,ds_tag_ini_w,nr_pos_w);

	if (coalesce(ie_manter_tag_externa_p, 'N') = 'N') then
	
		nr_substr_ini_w	:= length(ds_tag_ini_w) + nr_instr_ini_w;
	else
		
		nr_substr_ini_w	:= nr_instr_ini_w;
	end if;
	
	nr_tag_w	:= nr_tag_w + 1;
	nr_pos_w	:= nr_instr_ini_w + length(ds_tag_ini_w);
end loop;

-- volta a busca para pegar o fim
nr_tag_w := 1;
nr_pos_w := 1;
-- pega o fim
while(nr_tag_w <= nr_tag_fim_w) loop

	
	nr_instr_fim_w	:= instr(ds_arquivo_xml_p,ds_tag_fim_w,nr_pos_w);

	if (coalesce(ie_manter_tag_externa_p, 'N') = 'N') then
	
		nr_substr_fim_w	:= nr_instr_fim_w - (length(ds_tag_ini_w) + nr_instr_ini_w);
	else
		
		nr_substr_fim_w	:= nr_instr_fim_w - (nr_instr_ini_w)+length(ds_tag_fim_w);
	end if;
 	
	nr_tag_w	:= nr_tag_w + 1;
	nr_pos_w	:= nr_instr_fim_w + length(ds_tag_fim_w);
end loop;


ds_retorno_w	:= substr(ds_arquivo_xml_p,nr_substr_ini_w,nr_substr_fim_w);

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
