SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ptu_a950_pck.processa_a950_xml ( nr_seq_arq_xml_p pls_imp_arq_A950.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Processa o XML ja importado para o banco
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[ ]  Objetos do dicionario [X] Tasy (Delphi/Java) [ X] Portal [ X]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------
Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_xml_w				pls_imp_arq_A950.ds_xml%type;
ds_cabecalho_w			pls_imp_arq_A950.ds_xml%type;
nr_versao_transacao_w	pls_imp_arq_A950.nr_versao_transacao%type;

nr_guias_hm_w	bigint := 0;

BEGIN

-- carrega o XML
begin
	select	ds_xml
	into STRICT	ds_xml_w
	from	pls_imp_arq_A950	a
	where	a.nr_sequencia		= nr_seq_arq_xml_p;
exception
	when no_data_found then null; -- nao da para usar o max em clob
end;

-- se tem arquivo, processa ele
if (ds_xml_w IS NOT NULL AND ds_xml_w::text <> '') then


	select	max(nr_versao_transacao)
	into STRICT	nr_versao_transacao_w
	from	pls_imp_arq_A950	a
	where	a.nr_sequencia		= nr_seq_arq_xml_p;

	-- pega o cabecalho e processa
	ds_cabecalho_w := ptu_a950_pck.obter_conteudo_tag(ds_xml_w,'cabecalhoTransacao',1, 1, 'N');
	CALL ptu_a950_pck.processa_cabecalho(nr_seq_arq_xml_p, ds_cabecalho_w, nm_usuario_p);
	
	-- processa as guias HM
	-- versoes anteriores a primeia separacao de processamento por versao
	if (nr_versao_transacao_w < 8) then

		CALL ptu_a950_pck.processa_guias_hm(nr_seq_arq_xml_p, ds_xml_w, nm_usuario_p);
	else	-- default vai ser a ultima versao
		CALL ptu_a950_pck.processa_guias_hm_v8(nr_seq_arq_xml_p, ds_xml_w, nm_usuario_p);
	end if;
	
	--processa as guias SADT
	if (nr_versao_transacao_w < 7) then

		CALL ptu_a950_pck.processa_guias_sadt(nr_seq_arq_xml_p, ds_xml_w, nm_usuario_p);

	elsif (nr_versao_transacao_w = 7) then
	
		CALL ptu_a950_pck.processa_guias_sadt_v7(nr_seq_arq_xml_p, ds_xml_w, nm_usuario_p);
	else --default vai ser a ultima versao
		CALL ptu_a950_pck.processa_guias_sadt_v8(nr_seq_arq_xml_p, ds_xml_w, nm_usuario_p);
	end if;
	
	commit;
	
end if;
	

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_a950_pck.processa_a950_xml ( nr_seq_arq_xml_p pls_imp_arq_A950.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
