SET client_encoding TO 'UTF8';


-- Converter a tag para data e hora
CREATE OR REPLACE FUNCTION ptu_aviso_imp_pck.converte_data_hora ( data_p text, hora_p text) RETURNS timestamp AS $body$
DECLARE

					
hora_w		varchar(255) := hora_p;


BEGIN
if (length(hora_w) > 8) then
	hora_w := substr(hora_w,1,8);
end if;

if (coalesce(data_p,'X') != 'X') and (coalesce(hora_w,'X') != 'X') then
	return to_date(data_p || ' ' || hora_w,'yyyy-mm-dd hh24:mi:ss');
else
	return null;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
