CREATE OR REPLACE FUNCTION ptu_aviso_pck.gera_seq_tiss (nr_seq_aviso_conta_p ptu_aviso_conta.nr_sequencia%type) RETURNS bigint AS $body$
DECLARE


nr_seq_item_tiss_w	ptu_aviso_material.nr_seq_item_tiss%type;


BEGIN

select 	coalesce(max(nr_seq), 0) + 1
into STRICT 	nr_seq_item_tiss_w
from (	SELECT 	max(pp.nr_seq_item_tiss) nr_seq
		from 	ptu_aviso_procedimento pp
		where 	pp.nr_seq_aviso_conta = nr_seq_aviso_conta_p
		
union

		SELECT 	max(pm.nr_seq_item_tiss) nr_seq
		from 	ptu_aviso_material pm
		where 	pm.nr_seq_aviso_conta = nr_seq_aviso_conta_p) alias4;
		
return 	nr_seq_item_tiss_w;	

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
