-- Gravar dados da geracao do arquivo A520



CREATE OR REPLACE PROCEDURE ptu_aviso_pck.gerar_arquivo_a520 ( nr_seq_arquivo_p ptu_aviso_arquivo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ds_hash_p ptu_aviso_arquivo.ds_hash%type) AS $body$
BEGIN

update	ptu_aviso_arquivo
set	ds_hash		= ds_hash_p,
	ie_status	= '3',
