	dt_geracao_arq	= clock_timestamp(),
	nm_arquivo	= 'AVE' ||to_char(dt_competencia,'yyyymm')||lpad(somente_numero(cd_unimed_origem),3,'0')||lpad(CASE WHEN nr_seq_arquivo = NULL THEN nr_sequencia  ELSE nr_seq_arquivo END ,7,'0') || '.xml'
where	nr_sequencia	= nr_seq_arquivo_p;

commit;
END;

