-- Gravar dados da geracao do arquivo A525



CREATE OR REPLACE PROCEDURE ptu_aviso_pck.gerar_arquivo_a525 ( nr_seq_arquivo_p ptu_aviso_arquivo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ds_hash_p ptu_aviso_arquivo.ds_hash%type) AS $body$
DECLARE

				
nm_arquivo_w		ptu_aviso_ret_arquivo.nm_arquivo%type;
				

BEGIN
select	replace(nm_arquivo,'E','R')
into STRICT	nm_arquivo_w
from	ptu_aviso_arquivo
where	nr_sequencia	= nr_seq_arquivo_p;	

update	ptu_aviso_ret_arquivo
set	ds_hash		= ds_hash_p,
	dt_geracao	= clock_timestamp(),
	nm_arquivo	= nm_arquivo_w
where	nr_seq_arquivo	= nr_seq_arquivo_p;

commit;
END;

$body$
