SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ptu_aviso_pck.limpar_prot_sem_conta_a520 ( nr_seq_lote_p ptu_lote_aviso.nr_sequencia%type) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Varre o lote, verificando quais os protocolo sem contas contas e apaga elas.
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:

Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


-- tabela virtual para a carga das contas

tb_nr_seq_protocolo_w	dbms_sql.number_table;

-- carrega os protocolos sem contas

c01 CURSOR(	nr_seq_lote_pc	ptu_lote_aviso.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia
	from	ptu_aviso_arquivo	a,
		ptu_aviso_protocolo	b
	where	b.nr_seq_arquivo		= a.nr_sequencia
	-- somente os que nao possuirem nenhuma conta

	and	not exists (	SELECT	1
					from	ptu_aviso_conta	x
					where	x.nr_seq_aviso_protocolo	= b.nr_sequencia)
	and	a.nr_seq_lote	= nr_seq_lote_pc;
	
	

BEGIN

-- abre o cursor e carrega as contas

open c01(nr_seq_lote_p);
loop
	fetch c01 bulk collect into tb_nr_seq_protocolo_w limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_protocolo_w.count = 0;
	
	-- apaga as contas

	forall i in tb_nr_seq_protocolo_w.first..tb_nr_seq_protocolo_w.last
		delete	FROM ptu_aviso_protocolo
		where	nr_sequencia	= tb_nr_seq_protocolo_w(i);
	
	commit;
	-- limpa o vetor

	tb_nr_seq_protocolo_w.delete;
	
end loop;

if (c01%isopen) then

	close c01;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
