-- Obter dados do arquivo A520



CREATE OR REPLACE FUNCTION ptu_aviso_pck.obter_dados_gb_a520 ( nr_seq_arquivo_p ptu_aviso_arquivo.nr_sequencia%type, ie_tipo_glosa_baixa_p ptu_aviso_glosa_bx_dados.ie_tipo_glosa_baixa%type, /* 1 - glosa / 2 - baixa */
 ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
if (ie_opcao_p = 'EX') then -- Verificar se existe
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	ptu_aviso_glosa_bx	 gb,
		ptu_aviso_glosa_bx_dados bd
	where	gb.nr_sequencia		= bd.nr_seq_glosa_baixa
	and	bd.ie_tipo_glosa_baixa	= ie_tipo_glosa_baixa_p
	and	gb.nr_seq_arquivo	= nr_seq_arquivo_p;
	
elsif (ie_opcao_p = 'PD') then -- Verificar se permite desfazer
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	ptu_aviso_glosa_bx	 gb,
		ptu_aviso_glosa_bx_dados bd
	where	gb.nr_sequencia		= bd.nr_seq_glosa_baixa
	and	gb.ie_status		in ('1','4') /* 1 - Pendente / 4 - Falha */

	and	gb.nr_seq_arquivo	= nr_seq_arquivo_p;
	
elsif (ie_opcao_p = 'RL') then
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	ptu_aviso_ret_arquivo	ra
	where	ra.nr_seq_arquivo	= nr_seq_arquivo_p;
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
