SET client_encoding TO 'UTF8';



-- obter os dados de configuracao do WS do A530



CREATE OR REPLACE FUNCTION ptu_aviso_pck.pls_obter_dados_ws_a530 (ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


/* Opcoes

SV?	Endereco do servlet, ja com o "?" concatenado
CP	Comunicacao Padrao Interna

*/


ds_retorno_w		varchar(255) := '';
nr_seq_webservice_w	pls_end_webservice_a530.nr_sequencia%type;


BEGIN

--levanta a configuracao vigente

select	max(nr_sequencia)
into STRICT	nr_seq_webservice_w
from	pls_end_webservice_a530
where	ie_situacao	= 'A'
and	clock_timestamp()		between trunc(coalesce(dt_inicio_vigencia, clock_timestamp()), 'dd') and fim_dia(coalesce(dt_fim_vigencia, clock_timestamp()));

if (ie_opcao_p = 'SV?') then

	select	max(ds_servlet)
	into STRICT	ds_retorno_w
	from	pls_end_webservice_a530
	where	nr_sequencia	= nr_seq_webservice_w;
	
	if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	
		ds_retorno_w := ds_retorno_w||'?';
	end if;

elsif (ie_opcao_p = 'CP') then

	select	max(ds_webservice_envio)
	into STRICT	ds_retorno_w
	from	pls_end_webservice_a530
	where	nr_sequencia	= nr_seq_webservice_w;
	
	ds_retorno_w := ptu_aviso_pck.include_link_delimiter(ds_retorno_w);

end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
