CREATE OR REPLACE FUNCTION ptu_aviso_pck.retorna_tipo_intercambio ( nr_seq_congenere_p pls_congenere.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Determina o tipo de intercambio conforme outorgante e congenere
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:	
			algoritmo pego da pls_obter_se_pagador_lote_fat
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


ds_retorno_w		varchar(1);
sg_estado_w		pessoa_juridica.sg_estado%type;
sg_estado_int_w		pessoa_juridica.sg_estado%type;
ie_nacional_w		pls_congenere.ie_nacional%type;

BEGIN

-- levanta o estado do outorgante

select	coalesce(max(b.sg_estado), 'X')
into STRICT	sg_estado_w
from	pls_outorgante		a,
	pessoa_juridica		b
where	a.cd_cgc_outorgante	= b.cd_cgc
and	a.cd_estabelecimento	= cd_estabelecimento_p;

select	coalesce(max(a.sg_estado), 'X'),
	coalesce(max(b.ie_nacional),'N')
into STRICT	sg_estado_int_w,
	ie_nacional_w
from	pessoa_juridica	a,
	pls_congenere	b
where	a.cd_cgc	= b.cd_cgc
and	b.nr_sequencia	= nr_seq_congenere_p;


if (ie_nacional_w = 'S') then

	ds_retorno_w := 'N'; -- nacional
	
elsif (sg_estado_w <> 'X') and (sg_estado_int_w <> 'X') then
	
	if (sg_estado_w = sg_estado_int_w) then
		ds_retorno_w	:= 'E'; -- estadual
	else	
		ds_retorno_w	:= 'N'; -- nacional
	end if;
else
	ds_retorno_w	:= 'A'; -- ambos	
	
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
