CREATE OR REPLACE PROCEDURE ptu_aviso_pck.valida_desfazer_lote_env_a520 (nr_seq_lote_p ptu_lote_aviso.nr_sequencia%type) AS $body$
DECLARE


nr_seq_pls_fatura_w	pls_fatura.nr_sequencia%type;
nr_seq_ptu_cobranca_w	ptu_nota_cobranca.nr_sequencia%type;

BEGIN



-- Valida se nao existe um A500 gerado para este lote

select	max(e.nr_seq_pls_fatura),
	max(d.nr_sequencia)
into STRICT	nr_seq_pls_fatura_w,
	nr_seq_ptu_cobranca_w
from	ptu_aviso_conta		a,
	ptu_aviso_protocolo	b,
	ptu_aviso_arquivo	c,
	ptu_nota_cobranca	d,
	ptu_fatura		e
where	b.nr_sequencia		= a.nr_seq_aviso_protocolo
and	c.nr_sequencia		= b.nr_seq_arquivo
and	d.nr_seq_aviso_conta	= a.nr_sequencia
and	e.nr_sequencia		= d.nr_seq_fatura
and	c.nr_seq_lote		= nr_seq_lote_p;


if (nr_seq_pls_fatura_w IS NOT NULL AND nr_seq_pls_fatura_w::text <> '') then

	--O lote esta contido na fatura: #@NR_SEQ_PLS_FATURA_P#@ e cobranca (PTU A500): #@NR_SEQ_COBRANCA_P#@, e nao pode ser desfeito.

	CALL wheb_mensagem_pck.exibir_mensagem_abort(1041594, 'NR_SEQ_PLS_FATURA_P='||to_char(nr_seq_pls_fatura_w)||';NR_SEQ_COBRANCA_P='||to_char(nr_seq_ptu_cobranca_w));	
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
