CREATE OR REPLACE PROCEDURE ptu_env_pck.ptu_cons_benef_retorno ( nr_seq_cons_benef_p ptu_consulta_beneficiario.nr_sequencia%type, ie_atualizou_p INOUT text) AS $body$
DECLARE


dt_nascimento_w		pessoa_fisica.dt_nascimento%type;
ie_sexo_w		pessoa_fisica.ie_sexo%type;
nr_seq_segurado_w	pls_segurado.nr_sequencia%type;
nr_seq_resp_cons_w	ptu_resp_consulta_benef.nr_sequencia%type;
nr_seq_resp_dados_w	ptu_resp_nomes_benef.nr_sequencia%type;
cd_usuario_plano_w	varchar(255);
cd_unimed_w		varchar(255);
ie_confirmacao_w	varchar(1) := 'N';


BEGIN

select	lpad(cd_unimed,4,'0'),
	lpad(cd_usuario_plano,13,'0')
into STRICT	cd_unimed_w,
	cd_usuario_plano_w
from	ptu_consulta_beneficiario
where	nr_sequencia = nr_seq_cons_benef_p;

select	max(nr_sequencia)
into STRICT	nr_seq_resp_cons_w
from	ptu_resp_consulta_benef
where	nr_seq_execucao	= nr_seq_cons_benef_p;

select	max(ie_confirmacao)
into STRICT	ie_confirmacao_w
from	ptu_resp_consulta_benef
where	nr_sequencia	= nr_seq_resp_cons_w;

if (ie_confirmacao_w IS NOT NULL AND ie_confirmacao_w::text <> '') and (ie_confirmacao_w = 'S') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_resp_dados_w
	from	ptu_resp_nomes_benef
	where	nr_seq_resp_benef = nr_seq_resp_cons_w
	and	lpad(cd_unimed,4,'0') = cd_unimed_w
	and	lpad(cd_usuario_plano,13,'0') = cd_usuario_plano_w;

	select	dt_nascimento,
		CASE WHEN ie_sexo=1 THEN 'M' WHEN ie_sexo=2 THEN 'I' WHEN ie_sexo=3 THEN 'F' END
	into STRICT	dt_nascimento_w,
		ie_sexo_w
	from	ptu_resp_nomes_benef
	where	nr_sequencia = nr_seq_resp_dados_w;

	select	max(nr_seq_segurado)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado_carteira
	where	cd_usuario_plano = cd_unimed_w || cd_usuario_plano_w;


	if (dt_nascimento_w IS NOT NULL AND dt_nascimento_w::text <> '') and (ie_sexo_w IS NOT NULL AND ie_sexo_w::text <> '') then
		begin
			update	pessoa_fisica
			set	dt_nascimento	= dt_nascimento_w,
				ie_sexo		= ie_sexo_w
			where	cd_pessoa_fisica = (	SELECT	max(cd_pessoa_fisica)
							from	pls_segurado
							where	nr_sequencia = nr_seq_segurado_w);

			commit;
		exception
		when others then
			nr_seq_segurado_w	:= null;
			dt_nascimento_w		:= null;
			ie_sexo_w		:= null;
		end;
	else
		begin
			update	pessoa_fisica
			set	dt_nascimento	= dt_nascimento_w,
				ie_sexo		= 'F'
			where	cd_pessoa_fisica = (	SELECT	max(cd_pessoa_fisica)
							from	pls_segurado
							where	nr_sequencia = nr_seq_segurado_w);

			commit;
		exception
		when others then
			nr_seq_segurado_w	:= null;
			dt_nascimento_w		:= null;
			ie_sexo_w		:= null;
		end;
	end if;

	if (dt_nascimento_w IS NOT NULL AND dt_nascimento_w::text <> '') or (ie_sexo_w IS NOT NULL AND ie_sexo_w::text <> '') then
		ie_atualizou_p := 'S';
	else
		ie_atualizou_p := 'N';
	end if;
else
	begin
		update	pessoa_fisica
		set	dt_nascimento	= dt_nascimento_w,
			ie_sexo		= 'F'
		where	cd_pessoa_fisica = (	SELECT	max(cd_pessoa_fisica)
						from	pls_segurado
						where	nr_sequencia = nr_seq_segurado_w);

		commit;
	exception
	when others then
		nr_seq_segurado_w	:= null;
		dt_nascimento_w		:= null;
		ie_sexo_w		:= null;
	end;

	ie_atualizou_p := 'N';
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_env_pck.ptu_cons_benef_retorno ( nr_seq_cons_benef_p ptu_consulta_beneficiario.nr_sequencia%type, ie_atualizou_p INOUT text) FROM PUBLIC;
