SET client_encoding TO 'UTF8';


--------------------------------------------------------------------------------00750 Comunicacao de internacao ou alta do beneficiario------------------------------------------------------------------------------------------------------------------------------------------



CREATE OR REPLACE PROCEDURE ptu_env_pck.ptu_env_00750_v70 ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, dt_evento_p ptu_comunicacao_internacao.dt_evento%type, ie_tipo_evento_p ptu_comunicacao_internacao.ie_tipo_evento%type, nr_motivo_encerr_p ptu_comunicacao_internacao.nr_seq_motivo_saida%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_comunica_alta_p INOUT ptu_comunicacao_internacao.nr_sequencia%type) AS $body$
DECLARE


nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
ie_recem_nascido_w		pls_requisicao.ie_recem_nascido%type;
ie_tipo_internacao_w		pls_requisicao.nr_seq_clinica%type;
nr_via_cartao_w			pls_segurado_carteira.nr_via_solicitacao%type;
nr_seq_resp_autoriz_w		ptu_resposta_autorizacao.nr_sequencia%type;
cd_unimed_executora_w		ptu_resposta_autorizacao.cd_unimed_executora%type;
cd_unimed_beneficiario_w	ptu_resposta_autorizacao.cd_unimed_beneficiario%type;
nr_seq_execucao_w		ptu_resposta_autorizacao.nr_seq_execucao%type;
nr_seq_origem_w			ptu_resposta_autorizacao.nr_seq_origem%type;
cd_unimed_w			ptu_resposta_autorizacao.cd_unimed%type;
cd_usuario_plano_w		ptu_resposta_autorizacao.cd_usuario_plano%type;


BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	nr_seq_segurado,
		coalesce(ie_recem_nascido, 'N'),
		nr_seq_clinica
	into STRICT	nr_seq_segurado_w,
		ie_recem_nascido_w,
		ie_tipo_internacao_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_requisicao_p;

	select	max(coalesce(nr_via_solicitacao,1))
	into STRICT	nr_via_cartao_w
	from	pls_segurado_carteira
	where	nr_seq_segurado	= nr_seq_segurado_w;

	select 	max(nr_sequencia)
	into STRICT	nr_seq_resp_autoriz_w
	from	ptu_resposta_autorizacao
	where	nr_seq_requisicao	= nr_seq_requisicao_p;

	if (nr_seq_resp_autoriz_w > 0) then
		select	cd_unimed_executora,
			cd_unimed_beneficiario,
			nr_seq_execucao,
			nr_seq_origem,
			cd_unimed,
			cd_usuario_plano
		into STRICT	cd_unimed_executora_w,
			cd_unimed_beneficiario_w,
			nr_seq_execucao_w,
			nr_seq_origem_w,
			cd_unimed_w,
			cd_usuario_plano_w
		from	ptu_resposta_autorizacao
		where	nr_sequencia	= nr_seq_resp_autoriz_w;
	end if;
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then

	select	nr_seq_segurado,
		coalesce(ie_recem_nascido, 'N'),
		nr_seq_clinica
	into STRICT	nr_seq_segurado_w,
		ie_recem_nascido_w,
		ie_tipo_internacao_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;

	select	max(coalesce(nr_via_solicitacao,1))
	into STRICT	nr_via_cartao_w
	from	pls_segurado_carteira
	where	nr_seq_segurado	= nr_seq_segurado_w;

	select 	max(nr_sequencia)
	into STRICT	nr_seq_resp_autoriz_w
	from	ptu_resposta_autorizacao
	where	nr_seq_guia	= nr_seq_guia_p;

	if (nr_seq_resp_autoriz_w > 0) then
		select	cd_unimed_executora,
			cd_unimed_beneficiario,
			nr_seq_execucao,
			nr_seq_origem,
			cd_unimed,
			cd_usuario_plano
		into STRICT	cd_unimed_executora_w,
			cd_unimed_beneficiario_w,
			nr_seq_execucao_w,
			nr_seq_origem_w,
			cd_unimed_w,
			cd_usuario_plano_w
		from	ptu_resposta_autorizacao
		where	nr_sequencia	= nr_seq_resp_autoriz_w;
	end if;
end if;

if (ie_tipo_internacao_w IS NOT NULL AND ie_tipo_internacao_w::text <> '') then
		begin
			select	coalesce((coalesce(cd_ptu_online, cd_ptu))::numeric , ie_tipo_internacao_w)
			into STRICT	ie_tipo_internacao_w
			from	pls_clinica
			where	nr_sequencia	= ie_tipo_internacao_w;
		exception
		when others then
			ie_tipo_internacao_w	:= null;
		end;
end if;

insert	into ptu_comunicacao_internacao(nr_sequencia, cd_transacao, ie_tipo_cliente,
	cd_unimed_executora, cd_unimed_beneficiario, nr_seq_execucao,
	cd_unimed, cd_usuario_plano, nr_via_cartao,
	ds_biometria, ie_recem_nascido, dt_evento,
	ie_tipo_evento, nr_seq_motivo_saida, ie_tipo_internacao,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, nr_versao, nr_seq_requisicao,
	nr_seq_guia, ds_token)
values (nextval('ptu_comunicacao_internacao_seq'), '00750', 'U',
	cd_unimed_executora_w, cd_unimed_beneficiario_w, nr_seq_execucao_w,
	cd_unimed_w, cd_usuario_plano_w, nr_via_cartao_w,
	'', ie_recem_nascido_w, dt_evento_p,
	ie_tipo_evento_p, CASE WHEN ie_tipo_evento_p='A' THEN  nr_motivo_encerr_p  ELSE null END , CASE WHEN ie_tipo_evento_p='I' THEN  ie_tipo_internacao_w  ELSE null END ,
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	nm_usuario_p, pls_obter_versao_scs, nr_seq_requisicao_p,
	nr_seq_guia_p, '') returning nr_sequencia into nr_seq_comunica_alta_p;

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	CALL pls_requisicao_gravar_hist( nr_seq_requisicao_p,'L',expressao_pck.obter_desc_expressao(962793) || cd_unimed_executora_w, '', nm_usuario_p );
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	CALL pls_guia_gravar_historico( nr_seq_guia_p, 2,expressao_pck.obter_desc_expressao(962793) || cd_unimed_executora_w, '', nm_usuario_p );
end if;

commit;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ptu_env_pck.ptu_env_00750_v70 ( nr_seq_guia_p pls_guia_plano.nr_sequencia%type, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, dt_evento_p ptu_comunicacao_internacao.dt_evento%type, ie_tipo_evento_p ptu_comunicacao_internacao.ie_tipo_evento%type, nr_motivo_encerr_p ptu_comunicacao_internacao.nr_seq_motivo_saida%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_comunica_alta_p INOUT ptu_comunicacao_internacao.nr_sequencia%type) FROM PUBLIC;
