-- Converte o tipo de despesa Tasy para o tipo de item Unimed 
CREATE OR REPLACE FUNCTION ptu_gerar_tabela_a1200_pck.obter_tipo_item ( ie_tipo_despesa_p text, ie_item_p text) RETURNS bigint AS $body$
DECLARE

 
ie_tipo_item_w		smallint;
				

BEGIN 
 
if (ie_item_p = 'P') then 
	 
	if (ie_tipo_despesa_p = 3) then 
		ie_tipo_item_w := 2;
	elsif (ie_tipo_despesa_p = 2) then 
		ie_tipo_item_w := 1;
	else 
		ie_tipo_item_w := 6;
	end if;
	 
elsif (ie_item_p = 'M') then 
 
	if (ie_tipo_despesa_p = 1) then 
		ie_tipo_item_w := 3;
	elsif (ie_tipo_despesa_p = 2) then 
		ie_tipo_item_w := 5;
	elsif (ie_tipo_despesa_p = 7) then 
		ie_tipo_item_w := 7;
	else 
		ie_tipo_item_w := 4;
	end if;
end if;
 
return ie_tipo_item_w;
 
END;

