CREATE OR REPLACE PROCEDURE ptu_imp_pck.ptu_atualizar_compl_v70 ( nr_seq_pedido_aut_compl_p ptu_pedido_compl_aut.nr_sequencia%type, ie_possui_pedido_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


C01 CURSOR(nr_seq_pedido_aut_pc ptu_pedido_autorizacao.nr_sequencia%type) FOR
	SELECT	a.ds_observacao,
		a.nr_seq_guia,
		a.nr_seq_requisicao,
		a.cd_unimed_executora,
		a.nr_seq_origem,
		(SELECT	b.nr_sequencia
		from    pls_segurado_carteira c,
			pls_segurado b
		where   c.nr_seq_segurado	= b.nr_sequencia
		and	c.cd_usuario_plano	= lpad(a.cd_unimed,4,0)||a.cd_usuario_plano) nr_seq_segurado,
		(select	c.nr_sequencia
		from	ptu_controle_execucao c
		where	c.nr_seq_pedido_compl = a.nr_sequencia) nr_seq_exec_control,
		(select	max(nr_sequencia)
		from	ptu_resposta_autorizacao c
		where	c.nr_seq_execucao	= a.nr_seq_execucao
		and	c.cd_unimed_executora	= a.cd_unimed_executora) nr_seq_resp_ped_aut
	from	ptu_pedido_compl_aut a
	where	a.nr_sequencia = nr_seq_pedido_aut_pc;

ie_alto_custo_w		ptu_pedido_autorizacao.ie_alto_custo%type;

BEGIN

--Carrega os anexos importados no pedido de autorizacao

for c01_w in C01( nr_seq_pedido_aut_compl_p ) loop

	-- Se ja existia um pedido de autorizacao na base, o sistema nao pode gerar outro

	if ( ie_possui_pedido_p = 'N' ) then
		CALL pls_confirma_token(nr_seq_pedido_aut_compl_p, 'PC', c01_w.nr_seq_guia, c01_w.nr_seq_requisicao, nm_usuario_p, cd_estabelecimento_p);

		--Select para verificar se o prestador e de alto custo,  este campo e informado no Pedido de Autorizacao 00600

		select	coalesce(max(ie_alto_custo),'X')
		into STRICT	ie_alto_custo_w
		from	ptu_pedido_autorizacao
		where	nr_seq_execucao		= c01_w.nr_seq_origem
		and	cd_unimed_executora	= c01_w.cd_unimed_executora
		and	(nr_seq_guia IS NOT NULL AND nr_seq_guia::text <> '');

		--Realiza a consistencia do pedido

		CALL ptu_atualiza_imp_ped_autor_scs(	c01_w.nr_seq_guia, c01_w.nr_seq_requisicao, c01_w.nr_seq_segurado,
						null, c01_w.ds_observacao, null,
						ie_alto_custo_w, trunc(clock_timestamp()), cd_estabelecimento_p,
						nm_usuario_p, c01_w.cd_unimed_executora, 'C');
	end if;

	--Se ja existir uma resposta para o pedido de autorizacao, nao e necessario gerar novamente

	if ( coalesce(c01_w.nr_seq_resp_ped_aut::text, '') = '' ) then
		--Gera a resposta do pedido de autorizacao

		CALL ptu_imp_pck.ptu_gerar_resp_pedido_aut_v70( c01_w.nr_seq_exec_control, cd_estabelecimento_p, nm_usuario_p);
	end if;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
