	ie_tipo_evento_p, nr_motivo_encerr_p, ie_tipo_internacao_p,
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	nm_usuario_p, nr_versao_p, nr_seq_requisicao_w,
	nr_seq_guia_w, ds_token_p) returning nr_sequencia into nr_seq_comunica_alta_p;

if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
	CALL pls_requisicao_gravar_hist(nr_seq_requisicao_w,'L', expressao_pck.obter_desc_expressao(962714) || cd_unimed_executora_p, '', nm_usuario_p );
