SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE ptu_imp_pck.ptu_processa_00605_v70 ( nr_seq_pedido_aut_compl_p ptu_pedido_compl_aut.nr_sequencia%type, ie_possui_pedido_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_resp_pedido_compl_p INOUT ptu_resposta_autorizacao.nr_sequencia%type) AS $body$
DECLARE


cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estabelecimento_w := cd_estabelecimento_p;

--Quando as transacoes sao geradas pelo WebService, nao existe estabelecimento definido, entao e verificado o estabeleicmento do parametro

if ( coalesce(cd_estabelecimento_w::text, '') = ''	) then
	cd_estabelecimento_w := ptu_obter_estab_padrao;
end if;

--Se ja existir um pedido de autorizacao so precisa gerar a resposta do pedido

if ( ie_possui_pedido_p = 'N' ) then
	--Rotina utilizada para gerar a Requisicao ou Autorizacao

	CALL ptu_gerar_guia_req_compl( nr_seq_pedido_aut_compl_p, nm_usuario_p, cd_estabelecimento_w);

	--Rotina utilizada para gerar os itens nos campos da Autorizacao ou Requisicao

	CALL ptu_gerar_itens_compl( nr_seq_pedido_aut_compl_p, nm_usuario_p, cd_estabelecimento_w);

	---Rotina utilizada para gerar os dados dos anexos na Autorizacao ou Requisicao

	CALL ptu_gerar_anexo_guia_req_compl( nr_seq_pedido_aut_compl_p, nm_usuario_p, cd_estabelecimento_w);
end if;

--Rotina utilizada para realizar a consistencia na Requisicao ou Autorizacao, e atualizar alguns campos

CALL ptu_imp_pck.ptu_atualizar_compl_v70( nr_seq_pedido_aut_compl_p, ie_possui_pedido_p, nm_usuario_p, cd_estabelecimento_w);

--Retorna a sequencia da resposta gerada para o 00605 - Pedido Complemento de Autorizacao, esta sequencia e utilizada pelo WebService para gerar o XML de resposta do pedido

select (select	max(nr_sequencia)
	from	ptu_resposta_autorizacao c
	where	c.nr_seq_execucao	= a.nr_seq_execucao
	and	c.cd_unimed_executora	= a.cd_unimed_executora) nr_seq_resp_ped_aut
into STRICT	nr_seq_resp_pedido_compl_p
from	ptu_pedido_compl_aut a
where	a.nr_sequencia = nr_seq_pedido_aut_compl_p;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
