CREATE OR REPLACE PROCEDURE ptu_imp_pck.ptu_processa_00700_v70 ( nr_seq_dec_prazo_p ptu_decurso_prazo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_confirmacao_p INOUT ptu_confirmacao.nr_sequencia%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Realizar a validacao do arquivo de 00700 - Decurso de Prazo do PTU
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[  ]  Objetos do dicionario [ x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ x] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao: Performace
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nr_seq_execucao_w	ptu_decurso_prazo.nr_seq_guia%type;
cd_unimed_executora_w	ptu_confirmacao.cd_unimed_executora%type;


BEGIN

cd_estabelecimento_w := cd_estabelecimento_p;

--Quando as transacoes sao geradas pelo WebService, nao existe estabelecimento definido, entao e verificado o estabeleicmento do parametro

if ( coalesce(cd_estabelecimento_w::text, '') = ''	) then
	cd_estabelecimento_w := ptu_obter_estab_padrao;
end if;

CALL ptu_imp_pck.ptu_gerar_confirmacao_v70(nr_seq_dec_prazo_p, cd_estabelecimento_w, 'DP', pls_obter_versao_scs, nm_usuario_p);

select	cd_unimed_executora,
	nr_seq_execucao
into STRICT	cd_unimed_executora_w,
	nr_seq_execucao_w
from	ptu_decurso_prazo
where	nr_sequencia = nr_seq_dec_prazo_p;

begin
	select	nr_sequencia
	into STRICT	nr_seq_confirmacao_p
	from	ptu_confirmacao
	where	nr_seq_execucao = nr_seq_execucao_w
	and	cd_unimed_executora = cd_unimed_executora_w
	and	ie_tipo_resposta = 'DP';
exception
when others then
	nr_seq_confirmacao_p := null;
end;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
