CREATE OR REPLACE PROCEDURE ptu_imp_pck.ptu_processa_00806_v70 ( nr_seq_ordem_serv_p ptu_requisicao_ordem_serv.nr_sequencia%type, ie_possui_pedido_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_ret_ordem_serv_p INOUT ptu_resposta_req_ord_serv.nr_sequencia%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Rotina utilizada para validacao da transacao 00806 - Ordem de servico do PTU
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[  X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;


BEGIN

cd_estabelecimento_w := cd_estabelecimento_p;

--Quando as transacoes sao geradas pelo WebService, nao existe estabelecimento definido, entao e verificado o estabeleicmento do parametro

if ( coalesce(cd_estabelecimento_w::text, '') = ''	) then
	cd_estabelecimento_w := ptu_obter_estab_padrao;
end if;

if (ie_possui_pedido_p = 'N') then

	ptu_gerar_ord_serv(nr_seq_ordem_serv_p, nm_usuario_p, cd_estabelecimento_w, nr_seq_ret_ordem_serv_p);

	CALL ptu_gerar_itens_ord_serv(nr_seq_ordem_serv_p, nr_seq_ret_ordem_serv_p, nm_usuario_p, cd_estabelecimento_w);

end if;

--Retorna a sequencia da resposta do 00806 - Ordem de servico do PTU, esta sequencia e utilizada para gerar o XML de resposta pelo Webservice

select (	select	max(nr_sequencia)
	from	ptu_resposta_req_ord_serv c
	where	c.nr_seq_execucao	= a.nr_sequencia
	and	c.cd_unimed_executora	= a.cd_unimed_executora) nr_seq_ret_ordem_serv
into STRICT	nr_seq_ret_ordem_serv_p
from	ptu_requisicao_ordem_serv a
where	a.nr_sequencia = nr_seq_ordem_serv_p;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
