CREATE OR REPLACE PROCEDURE ptu_moviment_benef_a1300_pck.gerar_numero_arquivo_pf ( nr_seq_lote_p ptu_mov_benef_pf_lote.nr_sequencia%type, qt_pessoas_a1300_p pls_parametros.qt_pessoas_a1300%type) AS $body$
DECLARE

	
	qt_movimentacao_pf_w	bigint;
	qt_mov_autogestao_w	bigint;
	qt_mov_exclusao_w	bigint;
	nr_arquivo_w		bigint;
	nr_arquivo_autogestao_w	bigint;
	nr_arquivo_exclusao_w	bigint;
	
	
BEGIN
	
	if (coalesce(qt_pessoas_a1300_p,0) > 0) then
		loop
			if (nr_arquivo_w IS NOT NULL AND nr_arquivo_w::text <> '') then
				nr_arquivo_w	:= nr_arquivo_w + 1;
			else
				nr_arquivo_w	:= 1;
			end if;

			select	count(1)
			into STRICT	qt_movimentacao_pf_w
			from	ptu_mov_benef_pf_lote
			where	coalesce(nr_arquivo::text, '') = ''
			and	nr_seq_lote = nr_seq_lote_p;
		
			update	ptu_mov_benef_pf_lote
			set	nr_arquivo = nr_arquivo_w
			where	coalesce(nr_arquivo::text, '') = ''
			and	nr_seq_lote = nr_seq_lote_p  LIMIT (qt_pessoas_a1300_p);
		
			exit when qt_movimentacao_pf_w <= qt_pessoas_a1300_p;
		end loop;

		loop
			if (nr_arquivo_autogestao_w IS NOT NULL AND nr_arquivo_autogestao_w::text <> '') then
				nr_arquivo_autogestao_w	:= nr_arquivo_autogestao_w + 1;
			else
				nr_arquivo_autogestao_w	:= 1;
			end if;

			select	count(1)
			into STRICT	qt_mov_autogestao_w
			from	ptu_mov_benef_autogestao
			where	coalesce(nr_arquivo::text, '') = ''
			and	nr_seq_lote = nr_seq_lote_p;
		
			update	ptu_mov_benef_autogestao
			set	nr_arquivo = nr_arquivo_autogestao_w
			where	coalesce(nr_arquivo::text, '') = ''
			and	nr_seq_lote = nr_seq_lote_p  LIMIT (qt_pessoas_a1300_p);
		
			exit when qt_mov_autogestao_w <= qt_pessoas_a1300_p;
		end loop;
		
		loop
			if (nr_arquivo_exclusao_w IS NOT NULL AND nr_arquivo_exclusao_w::text <> '') then
				nr_arquivo_exclusao_w	:= nr_arquivo_exclusao_w + 1;
			else
				nr_arquivo_exclusao_w	:= 1;
			end if;

			select	count(1)
			into STRICT	qt_mov_exclusao_w
			from	ptu_mov_benef_exclusao
			where	coalesce(nr_arquivo::text, '') = ''
			and	nr_seq_lote = nr_seq_lote_p;
		
			update	ptu_mov_benef_exclusao
			set	nr_arquivo = nr_arquivo_exclusao_w
			where	coalesce(nr_arquivo::text, '') = ''
			and	nr_seq_lote = nr_seq_lote_p  LIMIT (qt_pessoas_a1300_p);
		
			exit when qt_mov_exclusao_w <= qt_pessoas_a1300_p;
		end loop;
	else
		update	ptu_mov_benef_pf_lote
		set	nr_arquivo = 1
		where	nr_seq_lote = nr_seq_lote_p;
		
		update	ptu_mov_benef_autogestao
		set	nr_arquivo = 1
