-- Gerar dados para gerar o arquivo de prestadores
CREATE OR REPLACE PROCEDURE ptu_prest_inter_exp_pck.gerar_dados_arq ( nr_seq_lote_p pls_lote_prestador_interc.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_registro_w		w_ptu_envio_arq.nr_seq_apres%type := 0;

C01 CURSOR(nr_seq_lote_pc			pls_lote_prestador_interc.nr_sequencia%type) FOR
	SELECT	nr_sequencia,
		rpad(coalesce(cd_cgc_cpf,' '),14,' ') ||
		rpad(coalesce(ds_razao_social,' '),60,' ') ||
		lpad(ie_tipo,4,'0') ||
		rpad(coalesce(cd_municipio_ibge,' '),7,' ') ||
		coalesce(ie_contratacao,' ') ||
		coalesce(to_char(dt_contratacao,'ddmmyyyy'),'        ') ||
		rpad(coalesce(cd_cnes,' '),7,' ') ||
		coalesce(to_char(dt_vinculo,'ddmmyyyy'),'        ') ||
		coalesce(cd_classificacao,' ') ||
		lpad(coalesce(cd_ans_int,' '),6,' ') ||
		coalesce(ie_disponibilidade_serv,' ') ||
		coalesce(ie_urgencia_emergencia,' ') ds_conteudo,
		nr_seq_prestador
	from	pls_prestador_interc_movto
	where	nr_seq_lote	= nr_seq_lote_pc
	order by nr_sequencia;

BEGIN
-- Excluir registros arquivo
delete	FROM w_ptu_envio_arq
where	nm_usuario = nm_usuario_p;

for r_c01_w in c01( nr_seq_lote_p ) loop
	nr_seq_registro_w := nr_seq_registro_w + 1;

	-- Inserir registros arquivo
	insert into w_ptu_envio_arq(nr_sequencia,			dt_atualizacao,		nm_usuario,
		ds_conteudo,			nr_seq_apres)
	values (nextval('w_ptu_envio_arq_seq'),	clock_timestamp(),		nm_usuario_p,
		r_c01_w.ds_conteudo,		nr_seq_registro_w);
end loop;

update	pls_lote_prestador_interc
set	dt_exportacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_lote_p;

commit;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
