CREATE OR REPLACE FUNCTION qsm_messages_pck.qsm_remove_tech_descript ( ds_xml_p text) RETURNS text AS $body$
DECLARE


pos_w           integer := 0;
offset_w        integer := 0;
tam_xml_w       integer := coalesce(length(ds_xml_p),0);
segment_w       text;


BEGIN

pos_w 		:= dbms_lob.position('<technicalDescription>' in ds_xml_p);
offset_w 	:= dbms_lob.position('</technicalDescription>' in ds_xml_p);

if (pos_w > 0) then
	segment_w	:= ( substr(ds_xml_p,pos_w - 1) || '' || substr(ds_xml_p,tam_xml_w,(offset_w + coalesce(length('</technicalDescription>'),0))));
else
	segment_w	:= ds_xml_p;
end if;

return segment_w;

END;

