SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION qsm_nominal_pck.get_update_nominal_data_user ( nr_seq_fila_p bigint) RETURNS SETOF T_NOMINAL_DATA_USER AS $body$
DECLARE


C01 CURSOR(nm_usuario_pc intpd_fila_transmissao.nm_usuario%type) FOR
	SELECT	a.nm_usuario ds_id,
			obter_dados_pf(b.cd_pessoa_fisica, 'PNC') ds_fullname
	from	usuario a,
			pessoa_fisica b
	where	a.nm_usuario		= nm_usuario_pc
	and		a.cd_pessoa_fisica	= b.cd_pessoa_fisica;

nr_seq_documento_w		intpd_fila_transmissao.nr_seq_documento%type;
nm_usuario_w			intpd_fila_transmissao.nm_usuario%type;
r_nominal_data_user_w		r_nominal_data_user;

BEGIN
select	a.nr_seq_documento,
	a.nm_usuario
into STRICT	nr_seq_documento_w,
	nm_usuario_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia		= nr_seq_fila_p;

for r_C01_w in C01(nm_usuario_w) loop
	begin
	r_nominal_data_user_w := qsm_nominal_pck.limpar_atributos_data_user(r_nominal_data_user_w);

	r_nominal_data_user_w.ds_id		:= r_C01_w.ds_id;
	r_nominal_data_user_w.ds_fullname	:= r_C01_w.ds_fullname;

	RETURN NEXT r_nominal_data_user_w;
	end;
end loop;

if (	coalesce(r_nominal_data_user_w.ds_id::text, '') = '' and
	coalesce(r_nominal_data_user_w.ds_fullname::text, '') = '') then
		r_nominal_data_user_w.ds_id		:= nm_usuario_w;
		
		RETURN NEXT r_nominal_data_user_w;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
