----------------     GET_UPDATE_NOMINAL_VISIT
CREATE OR REPLACE FUNCTION qsm_nominal_pck.get_update_nominal_visit ( nr_seq_fila_p bigint) RETURNS SETOF T_UPDATE_NOMINAL_VISIT AS $body$
DECLARE


nr_seq_documento_w			intpd_fila_transmissao.nr_seq_documento%type;
ie_evento_w				intpd_fila_transmissao.ie_evento%type;
nr_seq_agrupador_w			intpd_fila_transmissao.nr_seq_agrupador%type;
ie_conversao_w				intpd_eventos_sistema.ie_conversao%type;
nr_seq_sistema_w			intpd_eventos_sistema.nr_seq_sistema%type;
nr_seq_projeto_xml_w			intpd_eventos_sistema.nr_seq_projeto_xml%type;
nr_seq_regra_w				intpd_eventos_sistema.nr_seq_regra_conv%type;
r_update_nominal_visit_w		r_update_nominal_visit;
diagnostico_doenca_chave_w		dbms_sql.varchar2_table;
nr_atendimento_w			atendimento_paciente.nr_atendimento%type;
				
C01 CURSOR(nr_atendimento_pc bigint) FOR
	SELECT	to_char(a.dt_entrada, 'YYYY-MM-DD"T"HH24:MI:SS') dt_begin,
		intpd_conv('ATEND_PACIENTE_UNIDADE', 'CD_DEPARTAMENTO', d.cd_departamento, nr_seq_regra_w, ie_conversao_w, 'E') ds_department,
		lpad(obter_conversao_301('C301_6_DEPARTAMENTO','DEPARTAMENTO_MEDICO',null,d.cd_departamento,'I'),4,'0') ds_department_type,
		to_char(a.dt_alta_medico, 'YYYY-MM-DD"T"HH24:MI:SS') dt_end,
		a.nr_atendimento ds_key,
		a.nr_seq_tipo_admissao_fat ds_type,
		d.cd_departamento ds_ward
	from	atendimento_paciente a,
		setor_atendimento s,
		departamento_setor d
	where	obter_setor_atendimento(a.nr_atendimento)	= s.cd_setor_atendimento
	and	s.cd_setor_atendimento				= d.cd_setor_atendimento
	and	a.nr_atendimento				= nr_atendimento_pc;

BEGIN
select	a.nr_seq_documento,
	a.ie_evento,
	a.nr_seq_agrupador,
	coalesce(b.ie_conversao,'I'),
	b.nr_seq_sistema,
	b.nr_seq_projeto_xml,
	b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
	ie_evento_w,
	nr_seq_agrupador_w,
	ie_conversao_w,
	nr_seq_sistema_w,
	nr_seq_projeto_xml_w,
	nr_seq_regra_w
from	intpd_fila_transmissao a,
	intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and	a.nr_sequencia		= nr_seq_fila_p
and 	1=2;

if (ie_evento_w = '82') then
	diagnostico_doenca_chave_w := obter_lista_string(nr_seq_documento_w, obter_desc_expressao(960897));

	nr_atendimento_w := diagnostico_doenca_chave_w(1);
elsif (ie_evento_w = '54') then
	nr_atendimento_w := nr_seq_agrupador_w;
elsif (ie_evento_w = '120') or (ie_evento_w = '114') then
	nr_atendimento_w := nr_seq_documento_w;
else
	nr_atendimento_w := 0;
end if;


for r_C01_w in C01(nr_atendimento_w) loop
	begin
	r_update_nominal_visit_w := qsm_nominal_pck.limpar_atributos_visit(r_update_nominal_visit_w);

	r_update_nominal_visit_w.dt_begin			:= r_C01_w.dt_begin;
	r_update_nominal_visit_w.ds_department			:= r_C01_w.ds_department;
	r_update_nominal_visit_w.ds_department_type		:= r_C01_w.ds_department_type;
	r_update_nominal_visit_w.dt_end				:= r_C01_w.dt_end;
	r_update_nominal_visit_w.ds_key				:= r_C01_w.ds_key;
	r_update_nominal_visit_w.ds_type			:= r_C01_w.ds_type;
	r_update_nominal_visit_w.ds_ward			:= r_C01_w.ds_ward;

	RETURN NEXT r_update_nominal_visit_w;
	end;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
