CREATE OR REPLACE FUNCTION rcm301_arquivo_pck.get_seq_estrutura_arq (dt_referencia_p timestamp, cd_estabelecimento_p bigint, cd_convenio_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_tipo_convenio_w	convenio.ie_tipo_convenio%type;

c01 CURSOR FOR
SELECT	nr_seq_estrut_arq
from	c301_versao_convenio a
where	cd_estabelecimento					= cd_estabelecimento_p
and	clock_timestamp() 						between dt_inicio_vigencia and coalesce(dt_fim_vigencia, clock_timestamp() + interval '1 days')
and	coalesce(cd_convenio,cd_convenio_p) 				= cd_convenio_p
and	coalesce(ie_tipo_convenio,ie_tipo_convenio_w) 		= ie_tipo_convenio_w
and	((coalesce(a.nr_seq_classif_conv::text, '') = '') or
	 exists (select	1
		from	convenio_classif x
		where	x.cd_convenio		= cd_convenio_p
		and	x.nr_seq_classificacao	= a.nr_seq_classif_conv))
order by dt_inicio_vigencia,
	coalesce(ie_tipo_convenio,0),
	coalesce(nr_seq_classif_conv,0),
	coalesce(cd_convenio,0);


BEGIN

select	max(ie_tipo_convenio)
into STRICT	ie_tipo_convenio_w
from	convenio
where	cd_convenio	= cd_convenio_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	nr_sequencia_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
