CREATE OR REPLACE PROCEDURE reg_documento_pck.reg_cancel_revision ( nr_seq_version_revision_p bigint, nm_usuario_p text ) AS $body$
BEGIN
		if (nr_seq_version_revision_p IS NOT NULL AND nr_seq_version_revision_p::text <> '') then
			update	reg_version_revision
			set	ie_status_revisao = 'C', -- revisao cancelada
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_seq_version_revision_p;
			
		end if;

		commit;

	END;

$body$
