CREATE OR REPLACE PROCEDURE reg_test_plan_pck.remove_pendencies ( nm_usuario_p text, nr_seq_caso_teste_p bigint ) AS $body$
BEGIN

		if (nr_seq_caso_teste_p IS NOT NULL AND nr_seq_caso_teste_p::text <> '') then

			update	reg_tc_pendencies
			set	ds_motivo_exclusao 	= obter_desc_expressao_idioma(961679, 'Excluido devido a desaprovacao do caso de teste', 5),
				ie_status 		= 'E',
				nm_usuario 		= nm_usuario_p,
				dt_atualizacao 		= clock_timestamp()
			where	nr_seq_tc 	= nr_seq_caso_teste_p
			and	coalesce(ds_version::text, '') = '';

		end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
