CREATE OR REPLACE PROCEDURE rep_gerar_resumo_pck.gerar_rep_resumo_gasoterapia () AS $body$
DECLARE


	ds_procedimento_w		varchar(100);
	ds_intervalo_w			varchar(80);
	ie_suspenso_w			varchar(1);
	ds_observacao_w			varchar(255);

	C01 CURSOR FOR
	SELECT	substr(substr(obter_descricao_padrao('GAS', 'DS_GAS', nr_seq_gas),1,254) || ' - ' || CASE WHEN coalesce(IE_DISP_RESP_ESP::text, '') = '' THEN QT_GASOTERAPIA|| ' ' || IE_UNIDADE_MEDIDA  ELSE substr(obter_valor_dominio(1612,IE_DISP_RESP_ESP) ||' - '||QT_GASOTERAPIA|| ' ' || IE_UNIDADE_MEDIDA,1,255) END  ,1,254),
		a.ie_suspenso,
		substr(a.ds_observacao,1,255)
	from	prescr_gasoterapia a
	where	a.nr_prescricao	= nr_prescricao_w;
	
	
BEGIN
	PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', '', false);
	open C01;
	loop
	fetch C01 into	
		ds_procedimento_w,
		ie_suspenso_w,
		ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (current_setting('rep_gerar_resumo_pck.ie_rtf_w')::varchar(1) = 'N') then
			PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text ||
					rep_gerar_resumo_pck.get_tr_html(ds_procedimento_w, null, null,
							null, null, null, null, ie_suspenso_w,1),1,30000), false);			
							
			if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
				PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text || rep_gerar_resumo_pck.get_tr_html_obs(ds_observacao_w),1,30000), false);
			end if;
							
		else
			PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text ||
					rep_gerar_resumo_pck.get_tr_rtf(ds_procedimento_w, null, null,
							null, null, null, null, 1),1,30000), false);			
		end if;

		end;
	end loop;
	close C01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
