SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE rep_gerar_resumo_pck.gerar_rep_resumo_medic () AS $body$
DECLARE


	ds_material_w			varchar(400);
	qt_dose_w			prescr_material.qt_dose%type;
	ds_intervalo_w			varchar(80);
	ds_unidade_medida_w		varchar(100);
	ds_unid_med_w			varchar(160);
	ds_diluicao_w			varchar(2000);
	ie_suspenso_w			prescr_material.ie_suspenso%type;
	ds_horarios_w			prescr_material.ds_horarios%type;
	ds_observacao_w		prescr_material.ds_observacao%type;
	ds_justificativa_w		prescr_material.ds_justificativa%type;
	ds_dose_w				varchar(255);
	ie_via_aplicacao_w		prescr_material.ie_via_aplicacao%type;

	C01 CURSOR FOR
	SELECT	substr(coalesce(c.ds_reduzida, c.ds_material),1,255),
		a.qt_dose,
		substr(obter_desc_unid_med(a.cd_unidade_medida_dose),1,60),
		b.ds_intervalo || CASE WHEN a.ie_se_necessario='S' THEN ' - SN'  ELSE CASE WHEN a.ie_acm='S' THEN ' - ACM' END  END ,
		substr(a.ie_via_aplicacao,1,10),
		substr(obter_um_dosagem_prescr(a.nr_prescricao,a.nr_sequencia),1,50),
		substr(obter_diluicao_medic(a.nr_sequencia,a.nr_prescricao),1,255),
		a.ie_suspenso,
		a.ds_horarios,
		substr(a.ds_observacao,1,255),
		substr(a.ds_justificativa,1,255)
	FROM material c, prescr_material a
LEFT OUTER JOIN intervalo_prescricao b ON (a.cd_intervalo = b.cd_intervalo)
WHERE a.cd_material	= c.cd_material and a.nr_prescricao	= nr_prescricao_w and coalesce(a.nr_seq_Kit::text, '') = '' and a.ie_agrupador	= 1;

	
BEGIN
	
	PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', '', false);
	
	open C01;
	loop
	fetch C01 into	
		ds_material_w,
		qt_dose_w,
		ds_unidade_medida_w,
		ds_intervalo_w,
		ie_via_aplicacao_w,
		ds_unid_med_w,
		ds_diluicao_w,
		ie_suspenso_w,
		ds_horarios_w,
		ds_observacao_w,
		ds_justificativa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		ds_dose_w	:= to_char(qt_dose_w);
		if (qt_dose_w < 1) then
			ds_dose_w	:= '0' || to_char(qt_dose_w);
		end if;
		
		if (current_setting('rep_gerar_resumo_pck.ie_rtf_w')::varchar(1) = 'N') then
			PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text ||
					rep_gerar_resumo_pck.get_tr_html(ds_material_w, ds_dose_w , ds_unidade_medida_w, ds_intervalo_w, ie_via_aplicacao_w,
							ds_diluicao_w, ds_horarios_w, ie_suspenso_w,7),1,30000), false);
							
			if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
				PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text || rep_gerar_resumo_pck.get_tr_html_obs(ds_observacao_w),1,30000), false);
			end if;
			
			if (ds_justificativa_w IS NOT NULL AND ds_justificativa_w::text <> '') then
				PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text || rep_gerar_resumo_pck.get_tr_html_justif(ds_justificativa_w),1,30000), false);
			end if;				
							
		else
			PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text ||
					rep_gerar_resumo_pck.get_tr_rtf(ds_material_w, ds_dose_w , ds_unidade_medida_w, ds_intervalo_w, ie_via_aplicacao_w,
							ds_diluicao_w, ds_horarios_w,7),1,30000), false);
		end if;
				
		end;
	end loop;
	close C01;
	
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE rep_gerar_resumo_pck.gerar_rep_resumo_medic () FROM PUBLIC;
