SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE rep_gerar_resumo_pck.gerar_rep_resumo_proc () AS $body$
DECLARE


	ds_procedimento_w		varchar(100);
	ds_intervalo_w			varchar(80);
	ie_suspenso_w			varchar(1);
	ds_horarios_w			varchar(2000);
	ds_observacao_w			varchar(255);
	ds_justificativa_w		varchar(255);

	C01 CURSOR FOR
	SELECT	substr(Obter_Desc_Prescr_Proc(cd_procedimento, ie_origem_proced, nr_seq_proc_interno),1,80),
		a.ds_horarios,
		a.ie_suspenso,
		substr(a.ds_observacao,1,255),
		substr(a.ds_justificativa,1,255)
	from	prescr_procedimento a
	where	a.nr_prescricao	= nr_prescricao_w
	and	coalesce(a.nr_seq_origem::text, '') = ''
	and	coalesce(a.nr_seq_solic_sangue::text, '') = ''
	and	obter_se_exibe_proced(a.nr_prescricao,a.nr_sequencia,a.ie_tipo_proced,'O') = 'S';
	
	
BEGIN
	PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', '', false);
	open C01;
	loop
	fetch C01 into	
		ds_procedimento_w,
		ds_horarios_w,
		ie_suspenso_w,
		ds_observacao_w,
		ds_justificativa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (current_setting('rep_gerar_resumo_pck.ie_rtf_w')::varchar(1) = 'N') then
			PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text ||
					rep_gerar_resumo_pck.get_tr_html(ds_procedimento_w, ds_horarios_w, null, 
							null, null, null, null, ie_suspenso_w,2),1,30000), false);
							
			if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
				PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text || rep_gerar_resumo_pck.get_tr_html_obs(ds_observacao_w),1,30000), false);
			end if;
			
			if (ds_justificativa_w IS NOT NULL AND ds_justificativa_w::text <> '') then
				PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text || rep_gerar_resumo_pck.get_tr_html_justif(ds_justificativa_w),1,30000), false);
			end if;				
							
		else
			PERFORM set_config('rep_gerar_resumo_pck.ds_resumo_w', substr(current_setting('rep_gerar_resumo_pck.ds_resumo_w')::text ||
					rep_gerar_resumo_pck.get_tr_rtf(ds_procedimento_w, ds_horarios_w, null, 
							null, null, null, null, 2),1,30000), false);

		end if;
		
		end;
	end loop;
	close C01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE rep_gerar_resumo_pck.gerar_rep_resumo_proc () FROM PUBLIC;
