CREATE OR REPLACE FUNCTION san_gerar_codigo_isbt.san_obter_vlpos_antigeno ( ds_valor_p text, ds_antigeno1_p text, ds_antigeno2_p text) RETURNS bigint AS $body$
DECLARE


ie_pos_neg_fen1_w     smallint;
ie_pos_neg_fen2_w     smallint;
nr_retorno_w          bigint := 9;


BEGIN

if (ds_valor_p IS NOT NULL AND ds_valor_p::text <> '') then
	ie_pos_neg_fen1_w := 2;
	ie_pos_neg_fen2_w := 2;

	if (position(ds_antigeno1_p in ds_valor_p) <> 0 or position(ds_antigeno2_p in ds_valor_p) <> 0) then
		if (position(ds_antigeno1_p || '+' in ds_valor_p) <> 0) then
			ie_pos_neg_fen1_w := 1;
		end if;

		if (position(ds_antigeno1_p || '-' in ds_valor_p) <> 0) then
			ie_pos_neg_fen1_w := 0;
		end if;

		if (position(ds_antigeno2_p || '+' in ds_valor_p) <> 0) then
			ie_pos_neg_fen2_w := 1;
		end if;

		if (position(ds_antigeno2_p || '-' in ds_valor_p) <> 0) then
			ie_pos_neg_fen2_w := 0;
		end if;

		select max(vl_valor)
			into STRICT nr_retorno_w
		from SAN_TES_ESP_ANT_RES_ISBT
		where coalesce(dt_inativacao::text, '') = ''
			and cd_result_prim_antig = CASE WHEN ie_pos_neg_fen1_w=1 THEN  'POS' WHEN ie_pos_neg_fen1_w=0 THEN  'NEG' WHEN ie_pos_neg_fen1_w=2 THEN  'NT' END
			and cd_result_seg_antig = CASE WHEN ie_pos_neg_fen2_w=1 THEN  'POS' WHEN ie_pos_neg_fen2_w=0 THEN  'NEG' WHEN ie_pos_neg_fen2_w=2 THEN  'NT' END;

		if (coalesce(nr_retorno_w::text, '') = '' or nr_retorno_w = 9) then
			nr_retorno_w := 0;
		end if;
	end if;
end if;

return nr_retorno_w;
END;


$body$
