/*-----------------------------------------------------------------------------------------------------------------------------------------------------------------
Z_FI_KRED_HON_KIS- Fee
*/
CREATE OR REPLACE FUNCTION sap_fin_customer_pck.get_fee ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_FEE AS $body$
DECLARE


nr_seq_documento_w			intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w				intpd_eventos_sistema.ie_conversao%type;
nr_seq_sistema_w			intpd_eventos_sistema.nr_seq_sistema%type;
nr_seq_projeto_xml_w		intpd_eventos_sistema.nr_seq_projeto_xml%type;
nr_seq_regra_w				intpd_eventos_sistema.nr_seq_regra_conv%type;
r_get_fee_w					r_get_fee;
reg_integracao_w			gerar_int_padrao.reg_integracao_conv;
nr_seq_episodio_w			atendimento_paciente.nr_seq_episodio%type;
dt_atualizacao_w			timestamp;
dt_liberacao_w				timestamp;

C01 CURSOR FOR
	SELECT	nr_seq_tipo,
			dt_atualizacao,
			dt_liberacao,
			nr_repasse_terceiro,
      substr(ds_observacao, 0, 40) ds_observacao
	from	repasse_terceiro
	where	nr_repasse_terceiro = 	nr_seq_documento_w;
c01_w	c01%rowtype;


BEGIN
intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

select	a.nr_seq_documento,
		coalesce(b.ie_conversao,'I'),
		b.nr_seq_sistema,
		b.nr_seq_projeto_xml,
		b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
		ie_conversao_w,
		nr_seq_sistema_w,
		nr_seq_projeto_xml_w,
		nr_seq_regra_w
from	intpd_fila_transmissao a,
		intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and		a.nr_sequencia = nr_seq_fila_p;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		r_get_fee_w						:=	null;
		reg_integracao_w.nm_tabela 		:=	'REPASSE_TERCEIRO';
		reg_integracao_w.nm_elemento	:=	'Z_FI_KRED_HON_KIS';

		--intpd_processar_atrib_envio(reg_integracao_w, 'X','IV_BUKRS', 'N', 'c01_w.', 'N', r_get_fee_w.iv_bukrs);
		intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_TIPO', 'IV_BLART', 'N', c01_w.nr_seq_tipo, 'N', r_get_fee_w.iv_blart);

		begin
		dt_atualizacao_w		:=	to_char(c01_w.dt_atualizacao,'yyyy-mm-dd');
		exception
		when others then
			dt_atualizacao_w	:=	null;
		end;
		intpd_processar_atrib_envio(reg_integracao_w, 'DT_ATUALIZACAO', 'IV_BLDAT', 'N', dt_atualizacao_w, 'N', r_get_fee_w.iv_bldat);

		begin
		dt_liberacao_w		:=	to_char(c01_w.dt_liberacao,'yyyy-mm-dd');
		exception
		when others then
			dt_liberacao_w	:=	null;
		end;
		intpd_processar_atrib_envio(reg_integracao_w, 'DT_LIBERACAO', 'IV_BUDAT', 'N', dt_liberacao_w, 'N', r_get_fee_w.iv_budat);

		intpd_processar_atrib_envio(reg_integracao_w, 'NR_REPASSE_TERCEIRO', 'IV_XBLNR', 'N', c01_w.nr_repasse_terceiro, 'N', r_get_fee_w.iv_xblnr);
		intpd_processar_atrib_envio(reg_integracao_w, 'DS_OBSERVACAO','IV_BKTXT', 'N', c01_w.ds_observacao, 'N', r_get_fee_w.iv_bktxt);

		RETURN NEXT r_get_fee_w;
	end;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
