SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION sap_fin_customer_pck.get_invoice_items ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_INVOICE_ITEMS AS $body$
DECLARE


nr_seq_documento_w			intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w				  intpd_eventos_sistema.ie_conversao%type;
nr_seq_sistema_w			  intpd_eventos_sistema.nr_seq_sistema%type;
nr_seq_projeto_xml_w		intpd_eventos_sistema.nr_seq_projeto_xml%type;
nr_seq_regra_w				  intpd_eventos_sistema.nr_seq_regra_conv%type;
reg_integracao_w			  gerar_int_padrao.reg_integracao_conv;
r_get_invoice_items_w		r_get_invoice_items;
nr_seq_episodio_w			  atendimento_paciente.nr_seq_episodio%type;
nr_item_trib_w          r_get_invoice_items_w.strkt%type;
conta_contabil_w        conta_contabil%rowtype;
credit_indicator_w      r_get_invoice_items_w.shkzg%type;
valor_tributo           nota_fiscal_item_trib.vl_tributo%type;

C01 CURSOR FOR
	SELECT nr_sequencia,
         nr_item_nf,	
         cd_material,
			   vl_total_item_nf,
			   nr_atendimento,
         cd_conta_contabil,
         cd_centro_custo,
         substr(ds_observacao, 0, 50) ds_observacao,
         cd_estabelecimento
	 from	nota_fiscal_item
	where	nr_sequencia_nf = nr_seq_documento_w;
c01_w	c01%rowtype;


BEGIN
intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

select	a.nr_seq_documento,
		coalesce(b.ie_conversao,'I'),
		b.nr_seq_sistema,
		b.nr_seq_projeto_xml,
		b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
		ie_conversao_w,
		nr_seq_sistema_w,
		nr_seq_projeto_xml_w,
		nr_seq_regra_w
from	intpd_fila_transmissao a,
		intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and		a.nr_sequencia = nr_seq_fila_p;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		r_get_invoice_items_w			:=	null;
		reg_integracao_w.nm_tabela 		:=	'NOTA_FISCAL_ITEM';
		reg_integracao_w.nm_elemento	:=	'Z_FI_DEBI_INV_KIS';

		begin
		select	obter_episodio_atendimento(c01_w.nr_atendimento)
		into STRICT	nr_seq_episodio_w
		;
		exception
		when others then
			nr_seq_episodio_w		:=	null;
		end;

    begin
      select max('X'), sum(a.vl_tributo)
        into STRICT nr_item_trib_w, 
             valor_tributo
        from nota_fiscal_item_trib a
       where a.nr_item_nf = c01_w.nr_item_nf;
    exception
    when others then
			nr_item_trib_w		:= null;
      valor_tributo     := null;
		end;
    
    begin
      select *
        into STRICT conta_contabil_w
        from conta_contabil a
       where a.cd_conta_contabil = c01_w.cd_conta_contabil;
    exception
    when others then
			conta_contabil_w		:=	null;
		end;
    
    begin
      select CASE WHEN obter_dados_conta_contabil(conta_contabil_w.cd_conta_contabil, c01_w.cd_estabelecimento, 'DC')='D' THEN  'S'  ELSE 'H' END 
        into STRICT credit_indicator_w
;
    exception
    when others then
			credit_indicator_w :=	null;
		end;
    
		intpd_processar_atrib_envio(reg_integracao_w, 'NR_SEQ_EPISODIO', 'FALLTXT', 'N', nr_seq_episodio_w, 'N', r_get_invoice_items_w.falltxt);
		--intpd_processar_atrib_envio(reg_integracao_w, 'X','KOART', 'N', 'c02_w.', 'N', r_get_invoice_items_w.koart);
		intpd_processar_atrib_envio(reg_integracao_w, 'CD_MATERIAL', 'KTONR', 'N', c01_w.cd_material, 'N', r_get_invoice_items_w.ktonr);
		intpd_processar_atrib_envio(reg_integracao_w, 'X','SHKZG', 'N', credit_indicator_w, 'N', r_get_invoice_items_w.shkzg);
		intpd_processar_atrib_envio(reg_integracao_w, 'CD_CONTA_CONTA_CONTABIL','HKONT', 'N', c01_w.cd_conta_contabil, 'N', r_get_invoice_items_w.hkont);
		intpd_processar_atrib_envio(reg_integracao_w, 'VL_TOTAL_ITEM_NF', 'BETRAG', 'N', c01_w.vl_total_item_nf, 'N', r_get_invoice_items_w.betrag);
		--intpd_processar_atrib_envio(reg_integracao_w, 'VL_TRIBUTO','MWSKZ', 'N', valor_tributo, 'N', r_get_invoice_items_w.mwskz);
		intpd_processar_atrib_envio(reg_integracao_w, 'X','STRKT', 'N', nr_item_trib_w, 'N', r_get_invoice_items_w.strkt);
		intpd_processar_atrib_envio(reg_integracao_w, 'CD_CENTRO_CUSTO','KOSTL', 'N', c01_w.cd_centro_custo, 'N', r_get_invoice_items_w.kostl);
		intpd_processar_atrib_envio(reg_integracao_w, 'DS_OBSERVACAO','POSTXT', 'N', c01_w.ds_observacao, 'N', r_get_invoice_items_w.postxt);

		RETURN NEXT r_get_invoice_items_w;
	end;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION sap_fin_customer_pck.get_invoice_items ( nr_seq_fila_p bigint) FROM PUBLIC;
