SET client_encoding TO 'UTF8';


/*-----------------------------------------------------------------------------------------------------------------------------------------------------------------
Z_FI_DEBI_INV_KIS- Invoice
*/
CREATE OR REPLACE FUNCTION sap_fin_customer_pck.get_invoice ( nr_seq_fila_p bigint) RETURNS SETOF T_GET_INVOICE AS $body$
DECLARE


nr_seq_documento_w			intpd_fila_transmissao.nr_seq_documento%type;
ie_conversao_w				  intpd_eventos_sistema.ie_conversao%type;
nr_seq_sistema_w			  intpd_eventos_sistema.nr_seq_sistema%type;
nr_seq_projeto_xml_w		intpd_eventos_sistema.nr_seq_projeto_xml%type;
nr_seq_regra_w				  intpd_eventos_sistema.nr_seq_regra_conv%type;
r_get_invoice_w				  r_get_invoice;
reg_integracao_w			  gerar_int_padrao.reg_integracao_conv;
cd_cfop_w					      natureza_operacao.cd_cfop%type;
dt_entrada_w				    timestamp;
dt_emissao_w				    timestamp;
cd_natureza_operacao_w  operacao_nota.cd_natureza_operacao%type;

C01 CURSOR FOR
	SELECT	cd_natureza_operacao,
			dt_emissao,
			dt_entrada_saida,
			nr_nota_fiscal,
      substr(ds_observacao, 0, 40) ds_observacao
	from	nota_fiscal
	where	nr_sequencia = nr_seq_documento_w
	and		coalesce(dt_cancelamento::text, '') = '';
c01_w	c01%rowtype;


BEGIN
intpd_reg_integracao_inicio(nr_seq_fila_p, 'E', reg_integracao_w);

select	a.nr_seq_documento,
		coalesce(b.ie_conversao,'I'),
		b.nr_seq_sistema,
		b.nr_seq_projeto_xml,
		b.nr_seq_regra_conv
into STRICT	nr_seq_documento_w,
		ie_conversao_w,
		nr_seq_sistema_w,
		nr_seq_projeto_xml_w,
		nr_seq_regra_w
from	intpd_fila_transmissao a,
		intpd_eventos_sistema b
where	a.nr_seq_evento_sistema = b.nr_sequencia
and		a.nr_sequencia = nr_seq_fila_p;

open C01;
loop
fetch C01 into
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		r_get_invoice_w					:=	null;
		reg_integracao_w.nm_tabela 		:=	'NOTA_FISCAL';
		reg_integracao_w.nm_elemento	:=	'Z_FI_DEBI_INV_KIS';

		begin
		select	cd_cfop
		into STRICT	cd_cfop_w
		from	natureza_operacao
		where	cd_natureza_operacao = c01_w.cd_natureza_operacao;
		exception
		when others then
			cd_cfop_w		:=	null;
		end;

    begin
       select obter_dados_operacao_nota(c01_w.cd_natureza_operacao, 2)
         into STRICT cd_natureza_operacao_w
;
    exception
		when others then
			cd_natureza_operacao_w		:=	null;
		end;

		intpd_processar_atrib_envio(reg_integracao_w, 'CD_CFOP', 'IV_BUKRS', 'N', cd_cfop_w, 'N', r_get_invoice_w.iv_bukrs);

		intpd_processar_atrib_envio(reg_integracao_w, 'CD_NATUREZA_OPERACAO','IV_BLART', 'N', cd_natureza_operacao_w, 'N', r_get_invoice_w.iv_blart);

		begin
		dt_entrada_w		:=	to_char(c01_w.dt_entrada_saida,'yyyy-mm-dd');
		exception
		when others then
			dt_entrada_w	:=	null;
		end;
		intpd_processar_atrib_envio(reg_integracao_w, 'DT_ENTRADA', 'IV_BLDAT', 'N', dt_entrada_w, 'N', r_get_invoice_w.iv_bldat);

		begin
		dt_emissao_w		:=	to_char(c01_w.dt_emissao,'yyyy-mm-dd');
		exception
		when others then
			dt_emissao_w	:=	null;
		end;
		intpd_processar_atrib_envio(reg_integracao_w, 'DT_EMISSAO', 'IV_BUDAT', 'N', dt_emissao_w, 'N', r_get_invoice_w.iv_budat);

		intpd_processar_atrib_envio(reg_integracao_w, 'NR_NOTA_FISCAL', 'IV_XBLNR', 'N', c01_w.nr_nota_fiscal, 'N', r_get_invoice_w.iv_xblnr);
		
		intpd_processar_atrib_envio(reg_integracao_w, 'X','IV_BKTXT', 'N', c01_w.ds_observacao, 'N', r_get_invoice_w.iv_bktxt);

		RETURN NEXT r_get_invoice_w;

	end;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION sap_fin_customer_pck.get_invoice ( nr_seq_fila_p bigint) FROM PUBLIC;
