SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION search_panorama_content.get_if_comment_icon (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS bigint AS $body$
DECLARE

   ie_comment_w bigint;

   c_profile_icons_pat CURSOR FOR
      SELECT ri.cd_icone_panorama,
             ri.nr_seq_prioridade,
             ri.ds_icone_instituicao
        from regra_perm_panorama r, regra_panorama_icone_pac ri
       where r.nr_sequencia =
             (SELECT max(nr_sequencia)
                from regra_perm_panorama
               where cd_perfil = wheb_usuario_pck.get_cd_perfil)
         and ri.nr_seq_regra = r.nr_sequencia
         and ri.cd_icone_panorama = 93;

BEGIN
   ie_comment_w := 0;
   --Regra
   for r_profile_icons_pat in c_profile_icons_pat loop
      --
      Select 1
        into STRICT ie_comment_w
       from panorama_card_comment a
      where a.ie_situacao = 'A'
        and a.nr_atendimento = nr_atendimento_p  LIMIT 1;

   end loop;

   return(ie_comment_w);
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
